package de.fivethree.immoapps.backend;

import java.time.LocalDate;
import java.time.temporal.ChronoUnit;

public class Test {
    public static void main(String[] args) {

        LocalDate dateOne = LocalDate.parse("2019-01-12");
        LocalDate dateTwo = LocalDate.parse("2019-02-12");

        long monthbetween = Math.abs(ChronoUnit.MONTHS.between(dateOne, dateTwo));
        long daysbetween = Math.abs(ChronoUnit.DAYS.between(dateTwo, dateOne));
        System.out.println(monthbetween);
    }
}
