package de.fivethree.immoapps.backend.config;

import com.sun.mail.util.MailSSLSocketFactory;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.mail.javamail.JavaMailSender;
import org.springframework.mail.javamail.JavaMailSenderImpl;

import java.security.GeneralSecurityException;
import java.util.Properties;

@Configuration
public class MailConfig {
    @Bean
    public JavaMailSender getJavaMailSender() {
        JavaMailSenderImpl mailSender = new JavaMailSenderImpl();
        mailSender.setHost("smtp.immoapp.de");
        mailSender.setPort(25);

        mailSender.setUsername("appservice@immoapp.de");
        mailSender.setPassword("$$$Pw4Mailing");
        mailSender.setDefaultEncoding("UTF-8");

        Properties props = mailSender.getJavaMailProperties();
        props.put("mail.transport.protocol", "smtp");
        props.put("mail.smtp.auth", "true");
        props.put("mail.smtp.starttls.enable", "true");
        props.put("mail.smtp.starttls.required", "false");
        //This property is needed as long as the certificate is not trusted.
        props.put("mail.smtp.ssl.trust", "smtp.immoapp.de");
        //props.put("mail.smtp.ssl.enable", "true");
        //props.put("mail.debug", "true");


        return mailSender;
    }

}
