package de.fivethree.immoapps.backend.controller.webapp;

import de.fivethree.immoapps.backend.domain.checklist.PruefkriteriumCategory;
import de.fivethree.immoapps.backend.model.payload.res.checklist.GetPruefKriteriumsCategoriesResponse;
import de.fivethree.immoapps.backend.repository.checklist.PruefkriteriumCategoryRepository;
import de.fivethree.immoapps.backend.util.ModelMapper;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.security.access.prepost.PreAuthorize;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import java.util.List;

@RestController
@RequestMapping("/immoapps/api/pruefkriteriencategories")
public class PruefkriteriumCategoryController {

    @Autowired
    private PruefkriteriumCategoryRepository pruefkriteriumCategoryRepo;

    @GetMapping
    @PreAuthorize("hasRole('ADMIN') or hasRole('HAUSVERWALTER') or hasRole('MITARBEITER') or hasRole('USER')")
    public ResponseEntity<?> getAllCategories() {
        List<PruefkriteriumCategory> categories = this.pruefkriteriumCategoryRepo.findAll();
        return ResponseEntity.ok(new GetPruefKriteriumsCategoriesResponse(ModelMapper.mapPruefkriteriumsCategories(categories)));
    }
}
