package de.fivethree.immoapps.backend.controller.webapp;

import de.fivethree.immoapps.backend.domain.task.TaskScheduleOption;
import de.fivethree.immoapps.backend.model.payload.res.task.GetTaskScheduleOptionsResponse;
import de.fivethree.immoapps.backend.repository.task.TaskScheduleOptionRepository;
import de.fivethree.immoapps.backend.util.ModelMapper;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.security.access.prepost.PreAuthorize;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import java.util.List;

@RestController
@RequestMapping("/immoapps/api/taskscheduleoptions")
public class TaskScheduleOptionController {
    @Autowired
    private TaskScheduleOptionRepository taskScheduleOptionRepo;

    @GetMapping
    @PreAuthorize("hasRole('ADMIN') or hasRole('HAUSVERWALTER') or hasRole('MITARBEITER') or hasRole('USER')")
    public ResponseEntity<?> getAll() {
        List<TaskScheduleOption> options = this.taskScheduleOptionRepo.findAll();
        return ResponseEntity.ok(new GetTaskScheduleOptionsResponse(ModelMapper.mapTaskScheduleOptions(options)));
    }
}
