package de.fivethree.immoapps.backend.domain.adminchecklist;

import de.fivethree.immoapps.backend.domain.checklist.IChecklistTemplate;

import javax.persistence.*;
import javax.validation.constraints.NotBlank;
import java.util.HashSet;
import java.util.Set;

@Entity
@Table(name = "admin_checklist_template")
public class AdminChecklistTemplate implements IChecklistTemplate {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @NotBlank
    @Column
    private String name;

    @Column
    private Boolean defaultChecklist = false;

    @OneToMany(fetch = FetchType.LAZY, mappedBy = "adminchecklisttemplate", cascade = {CascadeType.ALL}, orphanRemoval = true)
    private Set<AdminChecklistTemplateLevel> levels = new HashSet<>();

    public AdminChecklistTemplate() {
    }

    public AdminChecklistTemplate(String name) {
        this.name = name;
    }

    public AdminChecklistTemplate(String name, Boolean defaultChecklist) {
        this.name = name;
        this.defaultChecklist = defaultChecklist;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public Set<AdminChecklistTemplateLevel> getLevels() {
        return levels;
    }

    public void setLevels(Set<AdminChecklistTemplateLevel> levels) {
        this.levels = levels;
    }

    public Boolean getDefaultChecklist() {
        return defaultChecklist;
    }

    public void setDefaultChecklist(Boolean defaultChecklist) {
        this.defaultChecklist = defaultChecklist;
    }
}
