package de.fivethree.immoapps.backend.domain.checklisttemplate;

import de.fivethree.immoapps.backend.domain.checklist.IChecklistLevel;

import javax.persistence.*;
import javax.validation.constraints.NotBlank;
import java.util.HashSet;
import java.util.Set;

@Entity
@Table(name = "checklist_template_level")
public class ChecklistTemplateLevel implements IChecklistLevel {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @NotBlank
    @Column
    private String name;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "checklist_template_id")
    private ChecklistTemplate checklistTemplate;

    @OneToMany(fetch = FetchType.LAZY, mappedBy = "level", cascade = {CascadeType.ALL}, orphanRemoval = true)
    private Set<ChecklistTemplatePruefkriterium> pruefkriterien = new HashSet<>();

    public ChecklistTemplateLevel() {
    }

    public ChecklistTemplateLevel(String name) {
        this.name = name;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public ChecklistTemplate getChecklistTemplate() {
        return checklistTemplate;
    }

    public void setChecklistTemplate(ChecklistTemplate checklistTemplate) {
        this.checklistTemplate = checklistTemplate;
    }

    public Set<ChecklistTemplatePruefkriterium> getPruefkriterien() {
        return pruefkriterien;
    }

    public void setPruefkriterien(Set<ChecklistTemplatePruefkriterium> pruefkriterien) {
        this.pruefkriterien = pruefkriterien;
    }
}
