package de.fivethree.immoapps.backend.domain.inspection;

import de.fivethree.immoapps.backend.domain.IDomain;
import de.fivethree.immoapps.backend.domain.checklist.ChecklistPruefkriterium;
import de.fivethree.immoapps.backend.domain.user.User;

import javax.persistence.*;
import java.util.HashSet;
import java.util.Set;

@Entity
@Table(name = "inspection_result")
public class InspectionResult implements IDomain {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @ManyToOne(fetch = FetchType.EAGER)
    @JoinColumn(name = "inspection_id")
    private Inspection inspection;

    @Column(name = "result")
    private Boolean result = false;

    @Column(name = "description")
    private String description;

    @OneToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "inspection_preufkriterium_id")
    private InspectionCriteriaPruefkriterium pruefkriterium;

    @OneToMany(fetch = FetchType.LAZY, mappedBy = "inspectionResult", cascade = {CascadeType.ALL}, orphanRemoval = true)
    private Set<InspectionImage> images = new HashSet<>();

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "user_id")
    private User assignedUser;

    public Set<InspectionImage> getImages() {
        return images;
    }

    public void setImages(Set<InspectionImage> images) {
        this.images = images;
    }

    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public Inspection getInspection() {
        return inspection;
    }

    public void setInspection(Inspection inspection) {
        this.inspection = inspection;
    }

    public Boolean getResult() {
        return result;
    }

    public void setResult(Boolean result) {
        this.result = result;
    }

    public InspectionCriteriaPruefkriterium getPruefkriterium() {
        return pruefkriterium;
    }

    public void setPruefkriterium(InspectionCriteriaPruefkriterium pruefkriterium) {
        this.pruefkriterium = pruefkriterium;
    }

    public User getAssignedUser() {
        return assignedUser;
    }

    public void setAssignedUser(User assignedUser) {
        this.assignedUser = assignedUser;
    }
}
