package de.fivethree.immoapps.backend.model.mail;

public class NewOpenInspectionMail {

    private String property;

    private String kriterienCount;

    private String hausverwaltung;

    private String auftragGeberFullname;

    private String auftragGeberEmail;

    public NewOpenInspectionMail(String property, String kriterienCount,
                                 String hausverwaltung, String auftragGeberFullname, String auftragGeberEmail) {
        this.property = property;
        this.kriterienCount = kriterienCount;
        this.hausverwaltung = hausverwaltung;
        this.auftragGeberFullname = auftragGeberFullname;
        this.auftragGeberEmail = auftragGeberEmail;
    }

    public String getProperty() {
        return property;
    }

    public String getKriterienCount() {
        return kriterienCount;
    }

    public String getHausverwaltung() {
        return hausverwaltung;
    }

    public String getAuftragGeberFullname() {
        return auftragGeberFullname;
    }

    public String getAuftragGeberEmail() {
        return auftragGeberEmail;
    }
}
