package de.fivethree.immoapps.backend.model.payload.req.inspection;

import javax.validation.constraints.NotNull;
import javax.validation.constraints.Size;
import java.util.List;

public class CreateInspectionRequest {
    @NotNull
    @Size(min = 1)
    private List<Long> inspectionCriterias;

    @NotNull
    private Long assignedUserId;

    public List<Long> getInspectionCriterias() {
        return inspectionCriterias;
    }

    public void setInspectionCriterias(List<Long> inspectionCriterias) {
        this.inspectionCriterias = inspectionCriterias;
    }

    public Long getAssignedUserId() {
        return assignedUserId;
    }

    public void setAssignedUserId(Long assignedUserId) {
        this.assignedUserId = assignedUserId;
    }
}
