package de.fivethree.immoapps.backend.model.payload.req.task;

import com.fasterxml.jackson.annotation.JsonFormat;
import org.springframework.format.annotation.DateTimeFormat;

import javax.validation.constraints.NotNull;
import java.time.LocalDate;

public class UpdateTaskRequest {

    @NotNull
    private String name;

    @NotNull
    private Boolean active;

    @NotNull
    private Long assignedUserId;

    @NotNull
    private Long scheduleOptionId;

    @NotNull
    @JsonFormat(pattern = "dd.MM.yyyy")
    private LocalDate startDate;

    public LocalDate getStartDate() {
        return startDate;
    }

    public void setStartDate(LocalDate startDate) {
        this.startDate = startDate;
    }

    public Long getAssignedUserId() {
        return assignedUserId;
    }

    public void setAssignedUserId(Long assignedUserId) {
        this.assignedUserId = assignedUserId;
    }

    public Long getScheduleOptionId() {
        return scheduleOptionId;
    }

    public void setScheduleOptionId(Long scheduleOptionId) {
        this.scheduleOptionId = scheduleOptionId;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public Boolean getActive() {
        return active;
    }

    public void setActive(Boolean active) {
        this.active = active;
    }
}
