package de.fivethree.immoapps.backend.model.payload.req.user;

import javax.validation.Valid;
import javax.validation.constraints.Email;
import javax.validation.constraints.NotBlank;
import javax.validation.constraints.Size;

public class UpdateUserRequest {
    @NotBlank
    private String firstname;

    @NotBlank
    private String lastname;

    @NotBlank
    @Size(min = 4)
    @Email
    private String email;

    @Valid
    private UpdateAdditionalUserInfoRequest updateAdditionalInfo;

    public UpdateAdditionalUserInfoRequest getUpdateAdditionalInfo() {
        return updateAdditionalInfo;
    }

    public void setUpdateAdditionalInfo(UpdateAdditionalUserInfoRequest updateAdditionalInfo) {
        this.updateAdditionalInfo = updateAdditionalInfo;
    }

    public String getFirstname() {
        return firstname;
    }

    public void setFirstname(String firstname) {
        this.firstname = firstname;
    }

    public String getLastname() {
        return lastname;
    }

    public void setLastname(String lastname) {
        this.lastname = lastname;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }
}
