package de.fivethree.immoapps.backend.model.payload.res.checklist;

import java.util.List;

public class ChecklistRep {
    private long id;

    private String name;

    private Boolean defaultChecklist;

    private List<ChecklistLevelRep> levels;

    public ChecklistRep(long id, String name, Boolean defaultChecklist, List<ChecklistLevelRep> levels) {
        this.id = id;
        this.name = name;
        this.defaultChecklist = defaultChecklist;
        this.levels = levels;
    }

    public Boolean getDefaultChecklist() {
        return defaultChecklist;
    }

    public void setDefaultChecklist(Boolean defaultChecklist) {
        this.defaultChecklist = defaultChecklist;
    }

    public List<ChecklistLevelRep> getLevels() {
        return levels;
    }

    public void setLevels(List<ChecklistLevelRep> levels) {
        this.levels = levels;
    }

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }
}
