package de.fivethree.immoapps.backend.model.payload.res.checklist.app;

import java.util.ArrayList;
import java.util.List;

public class ChecklistLevelAppRep {
    private long id;

    private String name;

    private List<ChecklistPruefkriteriumAppRep> kriterien = new ArrayList<>();

    public ChecklistLevelAppRep(long id, String name) {
        this.id = id;
        this.name = name;
    }

    public ChecklistLevelAppRep(long id, String name, List<ChecklistPruefkriteriumAppRep> kriterien) {
        this(id, name);
        this.kriterien = kriterien;
    }

    public List<ChecklistPruefkriteriumAppRep> getKriterien() {
        return kriterien;
    }

    public void setKriterien(List<ChecklistPruefkriteriumAppRep> kriterien) {
        this.kriterien = kriterien;
    }

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }
}
