package de.fivethree.immoapps.backend.model.payload.res.checklist.app;

import de.fivethree.immoapps.backend.model.payload.res.common.Base64ImageRep;
import de.fivethree.immoapps.backend.model.payload.res.checklist.ChecklistPruefKriteriumCategoryRep;

public class ChecklistPruefkriteriumAppRep {
    private Long id;

    private String name;

    private ChecklistPruefKriteriumCategoryRep category;

    private Base64ImageRep picto;

    public ChecklistPruefkriteriumAppRep(Long id, String name, ChecklistPruefKriteriumCategoryRep category,
                                         Base64ImageRep picto) {
        this.id = id;
        this.name = name;
        this.category = category;
        this.picto = picto;
    }

    public Base64ImageRep getPicto() {
        return picto;
    }

    public void setPicto(Base64ImageRep picto) {
        this.picto = picto;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public ChecklistPruefKriteriumCategoryRep getCategory() {
        return category;
    }

    public void setCategory(ChecklistPruefKriteriumCategoryRep category) {
        this.category = category;
    }
}
