package de.fivethree.immoapps.backend.model.payload.res.inspection;

import de.fivethree.immoapps.backend.model.payload.res.checklist.ChecklistPruefkriteriumRep;

public class InspectionCriteriaRep {
    private long id;

    private ChecklistPruefkriteriumRep pruefkriterium;

    public InspectionCriteriaRep(long id, ChecklistPruefkriteriumRep pruefkriterium) {
        this.id = id;
        this.pruefkriterium = pruefkriterium;
    }

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public ChecklistPruefkriteriumRep getPruefkriterium() {
        return pruefkriterium;
    }

    public void setPruefkriterium(ChecklistPruefkriteriumRep pruefkriterium) {
        this.pruefkriterium = pruefkriterium;
    }
}
