package de.fivethree.immoapps.backend.model.payload.res.inspection;

import java.util.ArrayList;
import java.util.List;

public class InspectionRep {
    private Boolean done;

    private Long id;

    private Long userId;

    private List<InspectionResultRep> results = new ArrayList<>();

    private List<InspectionCriteriaRep> criterias = new ArrayList<>();

    private Long propertyId;

    private Long auftragGeberId;

    public InspectionRep() {}

    public InspectionRep(Long id, Boolean done, Long userId, List<InspectionCriteriaRep> criterias,
                         List<InspectionResultRep> results, Long propertyId, Long auftragGeberId) {
        this.id = id;
        this.done = done;
        this.userId = userId;
        this.results = results;
        this.criterias = criterias;
        this.propertyId = propertyId;
        this.auftragGeberId = auftragGeberId;
    }

    public Long getAuftragGeberId() {
        return auftragGeberId;
    }

    public void setAuftragGeberId(Long auftragGeberId) {
        this.auftragGeberId = auftragGeberId;
    }

    public List<InspectionCriteriaRep> getCriterias() {
        return criterias;
    }

    public void setCriterias(List<InspectionCriteriaRep> criterias) {
        this.criterias = criterias;
    }

    public List<InspectionResultRep> getResults() {
        return results;
    }

    public void setResults(List<InspectionResultRep> results) {
        this.results = results;
    }

    public Boolean getDone() {
        return done;
    }

    public void setDone(Boolean done) {
        this.done = done;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public Long getUserId() {
        return userId;
    }

    public void setUserId(Long userId) {
        this.userId = userId;
    }

    public Long getPropertyId() {
        return propertyId;
    }

    public void setPropertyId(Long propertyId) {
        this.propertyId = propertyId;
    }
}
