package de.fivethree.immoapps.backend.model.payload.res.inspection;

import de.fivethree.immoapps.backend.model.payload.res.common.ImageRep;
import de.fivethree.immoapps.backend.model.payload.res.checklist.ChecklistPruefkriteriumRep;

import java.util.List;

public class InspectionResultRep {
    private long id;

    private boolean result;

    private ChecklistPruefkriteriumRep pruefkriterium;

    private String description;

    private List<ImageRep> images;

    public InspectionResultRep(long id, boolean result, ChecklistPruefkriteriumRep pruefkriterium,
                               String description, List<ImageRep> images) {
        this.id = id;
        this.result = result;
        this.pruefkriterium = pruefkriterium;
        this.description = description;
        this.images = images;
    }

    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    public List<ImageRep> getImages() {
        return images;
    }

    public void setImages(List<ImageRep> images) {
        this.images = images;
    }

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public boolean isResult() {
        return result;
    }

    public void setResult(boolean result) {
        this.result = result;
    }

    public ChecklistPruefkriteriumRep getPruefkriterium() {
        return pruefkriterium;
    }

    public void setPruefkriterium(ChecklistPruefkriteriumRep pruefkriterium) {
        this.pruefkriterium = pruefkriterium;
    }
}
