package de.fivethree.immoapps.backend.model.payload.res.inspection.app;

import de.fivethree.immoapps.backend.model.payload.res.inspection.InspectionCriteriaRep;

import java.util.ArrayList;
import java.util.List;

public class InspectionAppHistoryRep {
    private boolean done;

    private Long id;

    private Long userId;

    private List<InspectionResultAppHistoryRep> results = new ArrayList<>();

    private List<InspectionCriteriaAppRep> criterias = new ArrayList<>();

    private Long propertyId;

    public InspectionAppHistoryRep() {}

    public InspectionAppHistoryRep(Long id, boolean done, Long userId, List<InspectionCriteriaAppRep> criterias,
                                   List<InspectionResultAppHistoryRep> results, Long propertyId) {
        this.id = id;
        this.done = done;
        this.userId = userId;
        this.results = results;
        this.criterias = criterias;
        this.propertyId = propertyId;
    }

    public List<InspectionCriteriaAppRep> getCriterias() {
        return criterias;
    }

    public void setCriterias(List<InspectionCriteriaAppRep> criterias) {
        this.criterias = criterias;
    }

    public List<InspectionResultAppHistoryRep> getResults() {
        return results;
    }

    public void setResults(List<InspectionResultAppHistoryRep> results) {
        this.results = results;
    }

    public boolean isDone() {
        return done;
    }

    public void setDone(boolean done) {
        this.done = done;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public Long getUserId() {
        return userId;
    }

    public void setUserId(Long userId) {
        this.userId = userId;
    }

    public Long getPropertyId() {
        return propertyId;
    }

    public void setPropertyId(Long propertyId) {
        this.propertyId = propertyId;
    }
}
