package de.fivethree.immoapps.backend.model.payload.res.inspection.app;

import de.fivethree.immoapps.backend.model.payload.res.checklist.app.ChecklistPruefkriteriumAppRep;

public class InspectionCriteriaAppRep {
    private long id;

    private ChecklistPruefkriteriumAppRep pruefkriterium;

    public InspectionCriteriaAppRep(long id, ChecklistPruefkriteriumAppRep pruefkriterium) {
        this.id = id;
        this.pruefkriterium = pruefkriterium;
    }

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public ChecklistPruefkriteriumAppRep getPruefkriterium() {
        return pruefkriterium;
    }

    public void setPruefkriterium(ChecklistPruefkriteriumAppRep pruefkriterium) {
        this.pruefkriterium = pruefkriterium;
    }
}
