package de.fivethree.immoapps.backend.model.payload.res.inspection.app;

import de.fivethree.immoapps.backend.model.payload.res.common.Base64ImageRep;
import de.fivethree.immoapps.backend.model.payload.res.checklist.app.ChecklistPruefkriteriumAppRep;
import de.fivethree.immoapps.backend.model.payload.res.user.GetUserResponse;

import java.util.List;

public class InspectionResultAppRep {
    private long id;

    private boolean result;

    private ChecklistPruefkriteriumAppRep pruefkriterium;

    private String description;

    private List<Base64ImageRep> images;

    private GetUserResponse assignedUser;

    public InspectionResultAppRep(long id, boolean result, ChecklistPruefkriteriumAppRep pruefkriterium,
                                  String description, List<Base64ImageRep> images, GetUserResponse assignedUser) {
        this.id = id;
        this.result = result;
        this.pruefkriterium = pruefkriterium;
        this.description = description;
        this.images = images;
        this.assignedUser = assignedUser;
    }

    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    public List<Base64ImageRep> getImages() {
        return images;
    }

    public void setImages(List<Base64ImageRep> images) {
        this.images = images;
    }

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public boolean isResult() {
        return result;
    }

    public void setResult(boolean result) {
        this.result = result;
    }

    public ChecklistPruefkriteriumAppRep getPruefkriterium() {
        return pruefkriterium;
    }

    public void setPruefkriterium(ChecklistPruefkriteriumAppRep pruefkriterium) {
        this.pruefkriterium = pruefkriterium;
    }

    public GetUserResponse getAssignedUser() {
        return assignedUser;
    }

    public void setAssignedUser(GetUserResponse assignedUser) {
        this.assignedUser = assignedUser;
    }
}
