package de.fivethree.immoapps.backend.model.payload.res.user;

public class PushNotificationRep {
    private Long id;

    private String title;

    private String message;

    private String date;

    private Boolean readByUser;

    private GetUserResponse sender;

    private String targetId;

    private String type;

    public PushNotificationRep(Long id, String title, String message, String date, Boolean readByUser,
                               GetUserResponse sender, String targetId, String type) {
        this.id = id;
        this.title = title;
        this.message = message;
        this.date = date;
        this.readByUser = readByUser;
        this.sender = sender;
        this.targetId = targetId;
        this.type = type;
    }

    public String getTargetId() {
        return targetId;
    }

    public void setTargetId(String targetId) {
        this.targetId = targetId;
    }

    public String getType() {
        return type;
    }

    public void setType(String type) {
        this.type = type;
    }

    public GetUserResponse getSender() {
        return sender;
    }

    public void setSender(GetUserResponse sender) {
        this.sender = sender;
    }

    public Boolean getReadByUser() {
        return readByUser;
    }

    public void setReadByUser(Boolean readByUser) {
        this.readByUser = readByUser;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getTitle() {
        return title;
    }

    public void setTitle(String title) {
        this.title = title;
    }

    public String getMessage() {
        return message;
    }

    public void setMessage(String message) {
        this.message = message;
    }

    public String getDate() {
        return date;
    }

    public void setDate(String date) {
        this.date = date;
    }
}
