package de.fivethree.immoapps.backend.repository;

import de.fivethree.immoapps.backend.domain.user.PushMessage;
import de.fivethree.immoapps.backend.domain.user.User;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.security.core.parameters.P;
import org.springframework.stereotype.Repository;

import java.util.List;
import java.util.Set;

@Repository
public interface PushMessageRepository extends JpaRepository<PushMessage, Long> {
    @Query(value = "SELECT p FROM PushMessage p " +
            "LEFT JOIN FETCH p.user user " +
            "LEFT JOIN FETCH p.sender " +
            "WHERE user = :user")
    Set<PushMessage> getByUser(@Param("user") User user);

    @Query(value = "SELECT p FROM PushMessage p " +
            "LEFT JOIN FETCH p.user user " +
            "LEFT JOIN FETCH p.sender " +
            "WHERE user = :user AND p.id = :id")
    PushMessage getByIdAndUser(@Param("id") Long id, @Param("user") User user);
}
