package de.fivethree.immoapps.backend.repository;

import de.fivethree.immoapps.backend.domain.user.PushToken;
import de.fivethree.immoapps.backend.domain.user.User;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

import java.util.Collection;
import java.util.List;
import java.util.Set;

@Repository
public interface PushTokenRepository extends JpaRepository<PushToken, Long> {
    Set<PushToken> findByTokenAndUser(String token, User user);

    Set<PushToken> findByUser(User user);

    @Query(value = "SELECT t FROM PushToken t " +
            "LEFT JOIN FETCH t.user user " +
            "WHERE user IN(:users)")
    Set<PushToken> findByUsers(@Param("users") Collection<User> users);
}
