package de.fivethree.immoapps.backend.repository.checklist;

import de.fivethree.immoapps.backend.domain.adminchecklist.AdminChecklistTemplateLevel;
import de.fivethree.immoapps.backend.domain.adminchecklist.AdminChecklistTemplatePruefkriterium;
import de.fivethree.immoapps.backend.domain.picto.AdminPicto;
import de.fivethree.immoapps.backend.domain.picto.HausverwalterPicto;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

@Repository
public interface AdminChecklistPruefkriteriumRepository extends JpaRepository<AdminChecklistTemplatePruefkriterium, Long> {
    AdminChecklistTemplatePruefkriterium findByIdAndLevel(Long id, AdminChecklistTemplateLevel level);


    @Query(value = "SELECT COUNT(a) FROM AdminChecklistTemplatePruefkriterium a " +
            "LEFT JOIN a.picto picto " +
            "WHERE picto = :picto")
    Long countUsageOfPicto(@Param("picto") AdminPicto picto);
}
