package de.fivethree.immoapps.backend.repository.checklist;

import de.fivethree.immoapps.backend.domain.adminchecklist.AdminChecklistTemplate;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

import java.util.List;
import java.util.Set;

@Repository
public interface AdminChecklistTemplateRepository extends JpaRepository<AdminChecklistTemplate, Long> {
    @Query(value = "SELECT ac FROM AdminChecklistTemplate ac " +
            "LEFT JOIN FETCH ac.levels ls " +
            "LEFT JOIN FETCH ls.pruefkriterien pruefkriterien " +
            "LEFT JOIN FETCH pruefkriterien.category " +
            "LEFT JOIN FETCH pruefkriterien.picto")
    Set<AdminChecklistTemplate> getAllAndFetchAndLevelsAndFetchKriterien();


    @Query(value = "SELECT ac FROM AdminChecklistTemplate ac " +
            "LEFT JOIN FETCH ac.levels ls " +
            "LEFT JOIN FETCH ls.pruefkriterien pruefkriterien " +
            "LEFT JOIN FETCH pruefkriterien.category " +
            "LEFT JOIN FETCH pruefkriterien.picto " +
            "WHERE ac.id = :id")
    AdminChecklistTemplate findByIdAndFetchLevelsAndFetchKriterien(@Param("id") Long id);
}
