package de.fivethree.immoapps.backend.repository.checklist;

import de.fivethree.immoapps.backend.domain.checklist.Checklist;
import de.fivethree.immoapps.backend.domain.property.Property;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

import java.util.List;
import java.util.Set;

@Repository
public interface ChecklistRepository extends JpaRepository<Checklist, Long> {
    Checklist findByIdAndProperty(Long id, Property property);

    @Query(value = "SELECT c FROM Checklist c " +
            "LEFT JOIN FETCH c.levels ls " +
            "LEFT JOIN FETCH ls.pruefkriterien p " +
            "LEFT JOIN FETCH p.category " +
            "LEFT JOIN FETCH p.picto " +
            "WHERE c.id = :id AND c.property.id = :propertyId")
    Checklist findByIdAndPropertyAndFetchLevelsAndFetchKriterien(@Param("id") Long id, @Param("propertyId") Long propertyId);

    @Query(value = "SELECT c FROM Checklist c " +
            "LEFT JOIN FETCH c.levels ls " +
            "LEFT JOIN FETCH ls.pruefkriterien p " +
            "LEFT JOIN FETCH p.category " +
            "LEFT JOIN FETCH p.picto " +
            "WHERE c.property.id = :propertyId")
    Set<Checklist> getAllByPropertyAndFetchLevelsAndFetchKriterien(@Param("propertyId") Long propertyId);
}
