package de.fivethree.immoapps.backend.repository.checklist;

import de.fivethree.immoapps.backend.domain.picto.HausverwalterPicto;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

import java.util.List;
import java.util.Set;

@Repository
public interface HausverwalterPictoRepository extends JpaRepository<HausverwalterPicto, Long> {
    @Query(value = "SELECT p FROM HausverwalterPicto p WHERE p.id = :id AND p.hausverwalter.id = :hausverwalterId")
    HausverwalterPicto findByIdAndHausverwalterId(@Param("id") Long id, @Param("hausverwalterId") Long hausverwalterId);

    @Query(value = "SELECT p FROM HausverwalterPicto p WHERE p.hausverwalter.id = :hausverwalterId")
    Set<HausverwalterPicto> findAllByHausverwalter(@Param("hausverwalterId") Long hausverwalterId);
}
