package de.fivethree.immoapps.backend.repository.task;

import de.fivethree.immoapps.backend.domain.picto.HausverwalterPicto;
import de.fivethree.immoapps.backend.domain.task.TaskPruefkriterium;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;
import org.springframework.stereotype.Repository;

@Repository
public interface TaskPruefkriteriumRepository extends JpaRepository<TaskPruefkriterium, Long> {
    @Query(value = "SELECT COUNT(t) FROM TaskPruefkriterium t " +
            "LEFT JOIN t.picto picto " +
            "WHERE picto = :picto")
    Long countUsageOfPicto(@Param("picto") HausverwalterPicto picto);
}
