package de.fivethree.immoapps.backend.util.fcm;

import java.util.List;

class LegacyPush {
    private List<String> registration_ids;

    private FCMPushNotification notification;

    private FCMPushData data;

    public LegacyPush(List<String> registration_ids, FCMPushNotification notification) {
        this.registration_ids = registration_ids;
        this.notification = notification;
    }

    public List<String> getRegistration_ids() {
        return registration_ids;
    }

    public void setRegistration_ids(List<String> registration_ids) {
        this.registration_ids = registration_ids;
    }

    public FCMPushNotification getNotification() {
        return notification;
    }

    public void setNotification(FCMPushNotification notification) {
        this.notification = notification;
    }

    public FCMPushData getData() {
        return data;
    }

    public void setData(FCMPushData data) {
        this.data = data;
    }
}
