import { Property } from 'shared';
import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { ActionSheetController } from '@ionic/angular';
import { CommonService } from '../../services/common.service';

@Component({
  selector: 'app-property',
  templateUrl: './property.component.html',
  styleUrls: ['./property.component.scss']
})
export class PropertyComponent implements OnInit {


  @Input() property: Property;

  @Output() openProperty = new EventEmitter<Property>();
  @Output() startCheck = new EventEmitter<Property>();
  fabVisible = true;

  @Input() first = false;
  @Input() last = false;

  constructor(public actionSheet: ActionSheetController, private common: CommonService) { }

  ngOnInit() {
  }

  check(){
    this.startCheck.emit(this.property);
  }

  open(){
    this.openProperty.emit(this.property);
  }

  navigate(){
    this.common.openNavigation(this.property.address);
  }
}
