import { Injectable } from '@angular/core';
import { Platform } from '@ionic/angular';
import { InAppBrowser, InAppBrowserOptions } from '@ionic-native/in-app-browser/ngx';

@Injectable({
  providedIn: 'root'
})
export class CommonService {

  constructor(private platform: Platform, private iab: InAppBrowser) { }

  async openNavigation(address: string) {
    const options: InAppBrowserOptions = {
      closebuttoncaption: 'Zur√ºck',
      toolbarcolor: '#f4f4f4',
      location: 'no'
    };
    this.iab
      .create(`https://www.google.com/maps/dir/?api=1&destination=${address}&dir_action=navigate`, '_self', options)
      .show();
  }


}
