import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { GetPictoBase64Result } from 'shared';
import { environment } from '@environments/environment';

@Injectable({
  providedIn: 'root'
})
export class ImageService {

  constructor(private http: HttpClient) { }

  getPictoBase64(id: number, isAdmin?: boolean) {
    const url = isAdmin ? 'adminpicto' : 'hausverwalterpicto';
    return this.http.get<GetPictoBase64Result>(`${environment.baseURL}/api/${url}/base64/${id}`);
  }
}
