import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { environment } from '@environments/environment';
import { Observable } from 'rxjs';
import { MessageResponse, Message } from 'shared';

@Injectable({
  providedIn: 'root'
})
export class MessageService {

  constructor(private http: HttpClient) { }

  loadMessages(): Observable<MessageResponse> {
    return this.http
      .get<MessageResponse>(`${environment.baseURL}/appapi/push/messages`);
  }

  markMessageAsRead(messageId: number): Observable<Message> {
    return this.http
      .get<Message>(`${environment.baseURL}/appapi/push/messages/${messageId}`);
  }

}
