import { APP_STATES } from './app.state';
import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { NgxsModule } from '@ngxs/store';
import { NgxsLoggerPluginModule } from '@ngxs/logger-plugin';
import { NgxsReduxDevtoolsPluginModule } from '@ngxs/devtools-plugin';
import { NgxsDispatchPluginModule } from '@ngxs-labs/dispatch-decorator';
import { DEVTOOLS_REDUX_CONFIG, LOGGER_CONFIG, OPTIONS_CONFIG } from './store.config';
import { IonicModule } from '@ionic/angular';
import { NgxsIonicRouterPluginModule } from '@fivethree/ngxs-ionic-router-plugin';
import { NgxsAsyncStoragePluginModule } from '@ngxs-labs/async-storage-plugin';
import { StorageService } from '@services/storage.service';

@NgModule({
  imports: [
    CommonModule,
    IonicModule,
    NgxsModule.forRoot(APP_STATES, OPTIONS_CONFIG),
    NgxsIonicRouterPluginModule.forRoot(),
    NgxsDispatchPluginModule.forRoot(),
    NgxsAsyncStoragePluginModule.forRoot(StorageService),
    NgxsReduxDevtoolsPluginModule.forRoot(DEVTOOLS_REDUX_CONFIG),
    NgxsLoggerPluginModule.forRoot(LOGGER_CONFIG)
  ],
  exports: [NgxsModule]
})
export class NgxsStoreModule {}
