package de.fivethree.immoapps.mail.model;

import javax.validation.constraints.NotBlank;
import javax.validation.constraints.NotNull;
import javax.validation.constraints.Size;
import java.util.ArrayList;
import java.util.Collection;

public class SendHTMLMailRequest {
    @NotNull
    @NotBlank
    private String sender;

    @NotNull
    @NotBlank
    private String subject;

    @NotNull
    @NotBlank
    private String body;

    @NotNull
    @Size(min = 1)
    private Collection<String> receivers;

    private Collection<String> ccReceivers = new ArrayList<>();

    private Collection<String> bccReceivers = new ArrayList<>();

    public String getSender() {
        return sender;
    }

    public void setSender(String sender) {
        this.sender = sender;
    }

    public String getSubject() {
        return subject;
    }

    public void setSubject(String subject) {
        this.subject = subject;
    }

    public String getBody() {
        return body;
    }

    public void setBody(String body) {
        this.body = body;
    }

    public Collection<String> getReceivers() {
        return receivers;
    }

    public void setReceivers(Collection<String> receivers) {
        this.receivers = receivers;
    }

    public Collection<String> getCcReceivers() {
        return ccReceivers;
    }

    public void setCcReceivers(Collection<String> ccReceivers) {
        this.ccReceivers = ccReceivers;
    }

    public Collection<String> getBccReceivers() {
        return bccReceivers;
    }

    public void setBccReceivers(Collection<String> bccReceivers) {
        this.bccReceivers = bccReceivers;
    }
}
