import { Pipe, PipeTransform } from '@angular/core';
import { ChecklistTemplate } from 'shared';

@Pipe({
    name: 'checklistsfilter'
})

export class ChecklistsFilterPipe implements PipeTransform {
    transform(value: any, ...args: any[]): any {
        if(!value){
            return value;
        }
        // tslint:disable-next-line:max-line-length
        const ret = value.filter((checklist: ChecklistTemplate) => checklist.name.toLowerCase().includes(args[0].toLowerCase()));
        return ret;
    }
}
