import { Pipe, PipeTransform } from '@angular/core';
import {Property} from 'shared';

@Pipe({
    name: 'propertiesfilter'
})
export class PropertiesFilterPipe implements PipeTransform {
    transform(value: any, ...args: any[]): any {
        if(!value){
            return value;
        }
        // tslint:disable-next-line:max-line-length
        const ret = value.filter((property: Property) => property.name.toLowerCase().includes(args[0].toLowerCase()) || property.address.toLowerCase().includes(args[0].toLowerCase()));
        return ret;
    }
}
