import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'userfilter'
})

export class UserFilterPipe implements PipeTransform {
    transform(value: any, ...args: any[]): any {
        if(!value){
            return value;
        }
        const ret = value.filter(user => user.email.toLowerCase().includes(args[0].toLowerCase())  || user.firstname.toLowerCase().includes(args[0].toLowerCase())  || user.lastname.toLowerCase().includes(args[0].toLowerCase())  );
        return ret;
    }
}
