package de.fivethree.immoapps.backend.domain.adminchecklist;

import de.fivethree.immoapps.backend.domain.checklist.IChecklistLevel;

import javax.persistence.*;
import javax.validation.constraints.NotBlank;
import java.util.HashSet;
import java.util.Set;

@Entity
@Table(name = "admin_checklist_template_level")
public class AdminChecklistTemplateLevel implements IChecklistLevel {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @NotBlank
    @Column
    private String name;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "admin_checklist_template_id")
    private AdminChecklistTemplate adminchecklisttemplate;

    @OneToMany(fetch = FetchType.LAZY, mappedBy = "level", cascade = CascadeType.ALL, orphanRemoval = true)
    private Set<AdminChecklistTemplatePruefkriterium> pruefkriterien = new HashSet<>();

    public AdminChecklistTemplateLevel() {
    }

    public AdminChecklistTemplateLevel(String name) {
        this.name = name;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public AdminChecklistTemplate getAdminchecklisttemplate() {
        return adminchecklisttemplate;
    }

    public void setAdminchecklisttemplate(AdminChecklistTemplate adminchecklisttemplate) {
        this.adminchecklisttemplate = adminchecklisttemplate;
    }

    public Set<AdminChecklistTemplatePruefkriterium> getPruefkriterien() {
        return pruefkriterien;
    }

    public void setPruefkriterien(Set<AdminChecklistTemplatePruefkriterium> pruefkriterien) {
        this.pruefkriterien = pruefkriterien;
    }
}
