package de.fivethree.immoapps.backend.domain.adminchecklist;

import de.fivethree.immoapps.backend.domain.picto.AdminPicto;
import de.fivethree.immoapps.backend.domain.checklist.IChecklistPruefkriterium;
import de.fivethree.immoapps.backend.domain.checklist.PruefkriteriumCategory;

import javax.persistence.*;
import javax.validation.constraints.NotBlank;

@Entity
@Table(name = "admin_checklist_template_pruefkriterium")
public class AdminChecklistTemplatePruefkriterium implements IChecklistPruefkriterium {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @NotBlank
    @Column
    private String name;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "category_id")
    private PruefkriteriumCategory category;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "level_id")
    private AdminChecklistTemplateLevel level;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "picto_id")
    private AdminPicto picto;

    public AdminChecklistTemplatePruefkriterium() {

    }

    public AdminChecklistTemplatePruefkriterium(String name, PruefkriteriumCategory category) {
        this.name = name;
        this.category = category;
    }

    public AdminChecklistTemplatePruefkriterium(String name, PruefkriteriumCategory category, AdminPicto picto) {
        this.name = name;
        this.category = category;
        this.picto = picto;
    }

    public AdminPicto getPicto() {
        return picto;
    }

    public void setPicto(AdminPicto picto) {
        this.picto = picto;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public PruefkriteriumCategory getCategory() {
        return category;
    }

    public void setCategory(PruefkriteriumCategory category) {
        this.category = category;
    }

    public AdminChecklistTemplateLevel getLevel() {
        return level;
    }

    public void setLevel(AdminChecklistTemplateLevel level) {
        this.level = level;
    }
}
