package de.fivethree.immoapps.backend.domain.checklist;

import javax.persistence.*;
import javax.validation.constraints.NotBlank;
import java.util.HashSet;
import java.util.Set;

@Entity
@Table(name = "checklist_level")
public class ChecklistLevel implements IChecklistLevel {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @NotBlank
    @Column
    private String name;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "checklist_id")
    private Checklist checklist;

    @OneToMany(fetch = FetchType.LAZY, mappedBy = "level", cascade = CascadeType.ALL)
    private Set<ChecklistPruefkriterium> pruefkriterien = new HashSet<>();

    public ChecklistLevel(String name) {
        this.name = name;
    }

    public ChecklistLevel() {
    }

    @Override
    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    @Override
    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public Checklist getChecklist() {
        return checklist;
    }

    public void setChecklist(Checklist checklist) {
        this.checklist = checklist;
    }

    @Override
    public Set<ChecklistPruefkriterium> getPruefkriterien() {
        return pruefkriterien;
    }

    public void setPruefkriterien(Set<ChecklistPruefkriterium> pruefkriterien) {
        this.pruefkriterien = pruefkriterien;
    }
}
