package de.fivethree.immoapps.backend.domain.checklisttemplate;

import de.fivethree.immoapps.backend.domain.picto.HausverwalterPicto;
import de.fivethree.immoapps.backend.domain.checklist.IChecklistPruefkriterium;
import de.fivethree.immoapps.backend.domain.checklist.PruefkriteriumCategory;

import javax.persistence.*;
import javax.validation.constraints.NotBlank;

@Entity
@Table(name = "checklist_template_pruefkriterium")
public class ChecklistTemplatePruefkriterium implements IChecklistPruefkriterium {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @NotBlank
    @Column
    private String name;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "category_id")
    private PruefkriteriumCategory category;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "level_id")
    private ChecklistTemplateLevel level;

    @ManyToOne(fetch = FetchType.LAZY, cascade = CascadeType.PERSIST)
    @JoinColumn(name = "picto_id")
    private HausverwalterPicto picto;

    public ChecklistTemplatePruefkriterium() {}

    public ChecklistTemplatePruefkriterium(String name, PruefkriteriumCategory category) {
        this.name = name;
        this.category = category;
    }

    public ChecklistTemplatePruefkriterium(String name, PruefkriteriumCategory category, HausverwalterPicto picto) {
        this.name = name;
        this.category = category;
        this.picto = picto;
    }

    @Override
    public HausverwalterPicto getPicto() {
        return picto;
    }

    public void setPicto(HausverwalterPicto picto) {
        this.picto = picto;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public PruefkriteriumCategory getCategory() {
        return category;
    }

    public void setCategory(PruefkriteriumCategory category) {
        this.category = category;
    }

    public ChecklistTemplateLevel getLevel() {
        return level;
    }

    public void setLevel(ChecklistTemplateLevel level) {
        this.level = level;
    }
}
