package de.fivethree.immoapps.backend.domain.inspection;

import de.fivethree.immoapps.backend.domain.checklist.IChecklistPruefkriterium;
import de.fivethree.immoapps.backend.domain.checklist.PruefkriteriumCategory;
import de.fivethree.immoapps.backend.domain.picto.HausverwalterPicto;

import javax.persistence.*;
import javax.validation.constraints.NotBlank;

@Entity
@Table(name = "inspection_criteria_pruefkriterium")
public class InspectionCriteriaPruefkriterium implements IChecklistPruefkriterium {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @NotBlank
    @Column
    private String name;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "category_id")
    private PruefkriteriumCategory category;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "picto_id")
    private HausverwalterPicto picto;

    @OneToOne(fetch = FetchType.LAZY, optional = false)
    @JoinColumn(name = "inspection_criteria_id", nullable = false)
    private InspectionCriteria inspectionCriteria;

    public InspectionCriteriaPruefkriterium() {

    }

    public InspectionCriteriaPruefkriterium(String name, PruefkriteriumCategory category, HausverwalterPicto picto, InspectionCriteria inspectionCriteria) {
        this.name = name;
        this.category = category;
        this.picto = picto;
        this.inspectionCriteria = inspectionCriteria;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public PruefkriteriumCategory getCategory() {
        return category;
    }

    public void setCategory(PruefkriteriumCategory category) {
        this.category = category;
    }

    public HausverwalterPicto getPicto() {
        return picto;
    }

    public void setPicto(HausverwalterPicto picto) {
        this.picto = picto;
    }

    public InspectionCriteria getInspectionCriteria() {
        return inspectionCriteria;
    }

    public void setInspectionCriteria(InspectionCriteria inspectionCriteria) {
        this.inspectionCriteria = inspectionCriteria;
    }
}
