package de.fivethree.immoapps.backend.domain.picto;

import de.fivethree.immoapps.backend.domain.IImageEntity;
import de.fivethree.immoapps.backend.domain.user.User;
import org.springframework.web.servlet.support.ServletUriComponentsBuilder;

import javax.persistence.*;

@Entity
@Table(name = "hausverwalter_picto")
public class HausverwalterPicto implements IImageEntity {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @Column(name = "name")
    private String name;

    @Column(name = "type")
    private String type;

    @Lob
    @Column(name = "data")
    private byte[] data;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "hausverwalter_id")
    private User hausverwalter;

    public HausverwalterPicto() {
    }

    public HausverwalterPicto(String name, String type, byte[] data, User hausverwalter) {
        this.name = name;
        this.type = type;
        this.data = data;
        this.hausverwalter = hausverwalter;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getType() {
        return type;
    }

    public void setType(String type) {
        this.type = type;
    }

    public byte[] getData() {
        return data;
    }

    public void setData(byte[] data) {
        this.data = data;
    }

    public User getHausverwalter() {
        return hausverwalter;
    }

    public void setHausverwalter(User hausverwalter) {
        this.hausverwalter = hausverwalter;
    }

    @Override
    public String getUrl() {
        return ServletUriComponentsBuilder.fromCurrentContextPath()
                .path("/immoapps/api/hausverwalterpicto/" + getId())
                .toUriString();
    }
}
