package de.fivethree.immoapps.backend.domain.task;

import de.fivethree.immoapps.backend.exceptions.NotImplementedException;

public enum TaskScheduleOptionEnum {
    TAEGLICH(1L),
    TAEGLICH_SKIP_WEEKENDS(2L),
    EINMALIG(3L),
    WOECHENTLICH(4L),
    MONATLICH(5L);

    private Long id;

    TaskScheduleOptionEnum(Long id) {
        this.id = id;
    }

    public Long getId() {
        return this.id;
    }

    public static TaskScheduleOptionEnum getById(Long id) {
        for (TaskScheduleOptionEnum value : TaskScheduleOptionEnum.values()) {
            if (value.getId().equals(id)) {
                return value;
            }
        }

        throw new NotImplementedException("Kein passendes TaskScheduleOptionEnum zur Id: " + id + " gefunden");
    }

}
