package de.fivethree.immoapps.backend.domain.user;

import org.springframework.util.StringUtils;

import javax.persistence.*;
import javax.validation.constraints.NotBlank;

@Entity
@Table(name = "user_additional_info")
public class AdditionalUserInfo {
    @Id
    @GeneratedValue(generator = "sequence")
    @SequenceGenerator(name = "sequence", sequenceName = "MY_SEQ", allocationSize = 1, initialValue = 1000)
    private Long id;

    @NotBlank
    @Column
    private String companyName;

    @NotBlank
    @Column
    private String city;

    @NotBlank
    @Column
    private String zip;

    @NotBlank
    @Column
    private String streetAndNo;

    @Column
    private String telephone;

    @Column
    private String website;

    @Column
    private String fax;

    @OneToOne(fetch = FetchType.LAZY, optional = false)
    @JoinColumn(name = "user_id", nullable = false)
    private User user;

    public AdditionalUserInfo() {}

    public AdditionalUserInfo(User user, String companyName, String city, String zip, String streetAndNo, String telephone,
                              String website, String fax) {
        this.companyName = companyName;
        this.city = city;
        this.zip = zip;
        this.streetAndNo = streetAndNo;
        this.telephone = StringUtils.isEmpty(telephone) ? null : telephone;
        this.website = StringUtils.isEmpty(website) ? null : website;
        this.fax = StringUtils.isEmpty(fax) ? null : fax;
        this.user = user;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getCompanyName() {
        return companyName;
    }

    public void setCompanyName(String companyName) {
        this.companyName = companyName;
    }

    public String getCity() {
        return city;
    }

    public void setCity(String city) {
        this.city = city;
    }

    public String getZip() {
        return zip;
    }

    public void setZip(String zip) {
        this.zip = zip;
    }

    public String getStreetAndNo() {
        return streetAndNo;
    }

    public void setStreetAndNo(String streetAndNo) {
        this.streetAndNo = streetAndNo;
    }

    public String getTelephone() {
        return telephone;
    }

    public void setTelephone(String telephone) {
        this.telephone = telephone;
    }

    public String getWebsite() {
        return website;
    }

    public void setWebsite(String website) {
        this.website = website;
    }

    public String getFax() {
        return fax;
    }

    public void setFax(String fax) {
        this.fax = fax;
    }

    public User getUser() {
        return user;
    }

    public void setUser(User user) {
        this.user = user;
    }
}
