package de.fivethree.immoapps.backend.model.mail;

public class InspectionFinishedSummary {
    private String propertyName;

    private Long kriterienCount;

    private Long successCount;

    private Long issuesCount;

    private String inspectionUserName;

    private String inspectionUserEmail;

    public InspectionFinishedSummary(String propertyName, Long kriterienCount, Long successCount,
                                     Long issuesCount, String inspectionUserName, String inspectionUserEmail) {
        this.propertyName = propertyName;
        this.kriterienCount = kriterienCount;
        this.successCount = successCount;
        this.issuesCount = issuesCount;
        this.inspectionUserName = inspectionUserName;
        this.inspectionUserEmail = inspectionUserEmail;
    }

    public String getPropertyName() {
        return propertyName;
    }

    public Long getKriterienCount() {
        return kriterienCount;
    }

    public Long getSuccessCount() {
        return successCount;
    }

    public Long getIssuesCount() {
        return issuesCount;
    }

    public String getInspectionUserName() {
        return inspectionUserName;
    }

    public String getInspectionUserEmail() {
        return inspectionUserEmail;
    }
}
