package de.fivethree.immoapps.backend.model.payload.req.checklist;

import javax.validation.constraints.NotBlank;
import javax.validation.constraints.Size;
import java.util.ArrayList;
import java.util.List;

public class CreateChecklistTemplateRequest {
    @NotBlank
    @Size(min = 2)
    private String name;

    private Boolean defaultChecklist = false;

    private List<String> levels = new ArrayList<>();

    public List<String> getLevels() {
        return levels;
    }

    public void setLevels(List<String> levels) {
        this.levels = levels;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public Boolean getDefaultChecklist() {
        return defaultChecklist;
    }

    public void setDefaultChecklist(Boolean defaultChecklist) {
        this.defaultChecklist = defaultChecklist;
    }
}
