package de.fivethree.immoapps.backend.model.payload.req.inspection;

import de.fivethree.immoapps.backend.model.payload.req.common.AddBase64ImageRequest;

import javax.validation.constraints.NotNull;
import java.util.List;

public class AttachInspectionResult {
    @NotNull
    private Long pruefkriteriumId;

    @NotNull
    private Boolean result;

    private String description;

    private List<AddBase64ImageRequest> images;

    private Long assignedUserId;

    public Long getAssignedUserId() {
        return assignedUserId;
    }

    public void setAssignedUserId(Long assignedUserId) {
        this.assignedUserId = assignedUserId;
    }

    public List<AddBase64ImageRequest> getImages() {
        return images;
    }

    public void setImages(List<AddBase64ImageRequest> images) {
        this.images = images;
    }

    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    public Long getPruefkriteriumId() {
        return pruefkriteriumId;
    }

    public void setPruefkriteriumId(Long pruefkriteriumId) {
        this.pruefkriteriumId = pruefkriteriumId;
    }

    public Boolean getResult() {
        return result;
    }

    public void setResult(Boolean result) {
        this.result = result;
    }
}
