package de.fivethree.immoapps.backend.model.payload.res.admin;

public class EntityCreationReportRes {
    private Long mitarbeiterCreationCount;

    private Long userCreationCount;

    private Long propertyCreationCount;

    private Long inspectionsFinishedCount;

    public EntityCreationReportRes(Long mitarbeiterCreationCount, Long userCreationCount, Long propertyCreationCount,
                                   Long inspectionsFinishedCount) {
        this.mitarbeiterCreationCount = mitarbeiterCreationCount;
        this.userCreationCount = userCreationCount;
        this.propertyCreationCount = propertyCreationCount;
        this.inspectionsFinishedCount = inspectionsFinishedCount;
    }

    public Long getMitarbeiterCreationCount() {
        return mitarbeiterCreationCount;
    }

    public void setMitarbeiterCreationCount(Long mitarbeiterCreationCount) {
        this.mitarbeiterCreationCount = mitarbeiterCreationCount;
    }

    public Long getUserCreationCount() {
        return userCreationCount;
    }

    public void setUserCreationCount(Long userCreationCount) {
        this.userCreationCount = userCreationCount;
    }

    public Long getPropertyCreationCount() {
        return propertyCreationCount;
    }

    public void setPropertyCreationCount(Long propertyCreationCount) {
        this.propertyCreationCount = propertyCreationCount;
    }

    public Long getInspectionsFinishedCount() {
        return inspectionsFinishedCount;
    }

    public void setInspectionsFinishedCount(Long inspectionsFinishedCount) {
        this.inspectionsFinishedCount = inspectionsFinishedCount;
    }
}
